var af: Float = 25.2345556// 下面将看到af的值已经发生了改变,float只能接受6位有效数值println("af的值为：\(af)");// f1的类型被推断为Doublevar f1 = 5.12e2println("f1的值为:\(f1)")// f2的类型被推断为Double，其值转为10进制的数为：22.53125var f2 = 0x5.a2p2println("f2的值为:\(f2)")var a = 0.0// 5.0除以0.0将出现正无穷大println("5.0/a的值为：\(5.0 / a)")// 所有正无穷大都相等，所以下面将会输出trueprintln(5.0 / a == 50000 / 0.0);// -5.0除以0.0将出现负无穷大println("-5.0/a的值为：\(-5.0 / a)")// 所有负无穷大都相等，所以下面将会输出trueprintln(-5.0 / a == -50000 / 0.0);// 0.0除以0.0将出现非数var nan : Double = a / a;println("a/a的值为：\(nan)")// 非数与自己都不相等，所以下面将会输出falseprintln(nan == nan)