var str : String? = "crazyit.org"// str是String?类型的，不能赋值给String类型的s变量//var s : String = strif str != nil{	// 在str后添加!，即可获取String?类型变量实际存储的值，	// 该值是String类型，因此可赋值给String类型的s变量	var s : String = str!}else{	println("str为nil，不能强制解析")}var n1 : Int? = 20var n2 : Int? = 15;if n1 != nil && n2 != nil{	// n1和n2是Int?类型，因此不能参与加法运算	//let sum = n1 + n2	// 在变量后添加感叹号（!)即可强制解析可选类型的值	let sum = n1! + n2!	println(sum)}else{	println("n1或n2为nil，不能强制解析")}