var s1: String? = "fkit.org"// 对于String?可选类型，必须使用感叹号执行强制解析var tmp : String = s1!var s2 : String! = "crazyit.org"// 对于String!隐式可选类型，无需使用感叹号执行强制解析var tmp2 : String = s2println("tmp的值为：\(tmp)，tmp2的值为：\(tmp2)")var name: String!println(name)name = "疯狂软件"// 先判断隐式可选类型的name变量不为nil，然后才获取该变量的值if name != nil{	var tmp = name	println("name变量有值，其值为：\(name)")}// 使用可选绑定来获取name变量的值if let temp = name{	println("name变量有值，其值为：\(name)")}