var somePoint = (x:0, y:0)switch somePoint {	case (0, 0):		println("(0, 0)位于原点")	case (_, 0):		println("(\(somePoint.0), 0)位于X轴上")	case (0, _):		println("(0, \(somePoint.1))位于Y轴上")	case (0...Int.max , 0...Int.max):		println("(\(somePoint.0), \(somePoint.1))位于第一象限")	case (Int.min..<0 , 0...Int.max):		println("(\(somePoint.0), \(somePoint.1))位于第二象限")	case (Int.min..<0 , Int.min..<0):		println("(\(somePoint.0), \(somePoint.1))位于第三象限")		case (0...Int.max , Int.min..<0):		println("(\(somePoint.0), \(somePoint.1))位于第四象限")	default:		break}