var languages = ["Swift", "OC", "PHP", "Perl" , "Ruby" , "Go"]// 获取languages中数组中索引为1~4的元素let subRange = languages[1..<4]println(subRange)  // 输出[OC, PHP, Perl]// 将languages中数组中索引为2~4的元素替换成"C/C++" , "Python"languages[2...4] = ["C/C++" , "Python"]println(languages)  // 输出[Swift, OC, C/C++, Python, Go]// 将languages中数组中索引为1~2的元素替换成"a" , "b" , "c"languages[1...2] = ["a" , "b" , "c"]println(languages)  // 输出[Swift, a, b, c, Python, Go]// 清空数组languages[0..<languages.count] = []println(languages)  // 输出[]