let str = "fkit.orgabcxyz123crazyittianhijt"// 定义一个Dictionary，统计各字符的出现次数var status:[Character:Int] = [:]// 遍历str字符串中的字符，统计各字符的出现次数for ch in str{	// 获取ch字符已经出现的次数	var num = status[ch]	// 如果之前从未出现过ch字符，那么status字典中并不包含ch字符的统计信息，	// num将会等于nil	if num != nil	{		// 将ch的出现次数加1		status[ch] = num! + 1	}	else	{		// 在status数组中设置ch字符出现了一次		status[ch] = 1	}}println(status)var maxOccurs = 0// 遍历status的value集合，找出字符的最大出现次数for occurs in Array(status.values){	if occurs > maxOccurs	{		maxOccurs = occurs	}}// 找出最多出现次数对应的字符for (ch, occurs) in status{	if occurs == maxOccurs	{		println("出现最多的字符为 \(ch)，该字符出现\(occurs)次")	}}