// 定义一个函数，声明2个形参，返回值为Int型func max(x:Int , y:Int) -> Int{	// 定义一个变量z，该变量等于x、y中较大的值	var z = x > y ? x : y	// 返回变量z的值	return z}// 定义一个函数，声明一个形参，返回值为String类型func sayHi(name: String) -> String{	println("===正在执行sayHi()函数===")	return "\(name)，您好！"}var a = 6var b = 9// 调用max()函数，将函数返回值赋值给result变量var result = max(a , b)  // ①println("result:\(result)")// 调用sayHi()函数，直接输出函数的返回值println(sayHi("孙悟空"))  // ②