func fn(n:Int) -> Int{	if n == 0 {		return 1	}	else if n == 1 {		return 4	}	else {		// 函数中调用它自身，就是函数递归		return 2 * fn(n - 1) + fn(n - 2)	}}//func fn(n: Int) -> Int//{//	if n == 20 {//		return 1//	}//	else if n == 21 {//		return 4//	}//	else {//		// 函数中调用它自身，就是函数递归//		return fn(n + 2) - 2 * fn(n + 1)//	}//}// 输出fn(10)的结果println("fn(10)的结果是:\(fn(10))")