func girth(width: Double , height: Double) -> Double{	return Double(2) * (width + height)}println(girth(12 , 22.3))// 定义外部形参名func girth2(宽 width: Double , 高 height: Double) -> Double{	return Double(2) * (width + height)}// 调用函数时也需要使用外部参数名println(girth2(宽:12 , 高:22.3))// 使用简化形式指定外部形参名func girth3(#width: Double , #height: Double) -> Double{	return Double(2) * (width + height)}// 调用函数时也需要使用外部参数名println(girth3(width:12 , height:22.3))// 使用简化形式为第一个形参指定外部形参名，func girth4(#width: Double , height: Double) -> Double{	return Double(2) * (width + height)}// 调用函数时也需要使用外部参数名println(girth4(width:12 , 22.3))