class DataWrap{	var a :Int = 0	var b :Int = 0 }func swap(inout dw : DataWrap!){	// 下面三行代码实现dw的a、b两个成员变量的值交换	// 定义一个临时变量来保存dw对象的a成员变量的值	var tmp = dw.a	// 把dw对象的b成员变量的值赋给a成员变量	dw.a = dw.b	// 把临时变量tmp的值赋给dw对象的b成员变量	dw.b = tmp	println("swap()函数里，a成员变量的值是\(dw.a)"		+ "；b成员变量的值是\(dw.b)")	// 把dw直接赋值为nil	dw = nil  // ①}var dw : DataWrap! = DataWrap()dw.a = 6dw.b = 9swap(&dw)println("交换结束后，a成员变量的值是\(dw.a )" 	+ "；b成员变量的值是\(dw.b)")