// 定义一个计算平方的函数func square(val: Int) -> Int{	return val * val}// 定义一个计算立方的函数func cube(val: Int) -> Int{	return val * val * val}// 定义一个计算阶乘的函数func factorial(val: Int) -> Int{	var result = 1	for index in 2...val	{		result *= index	}	return result}// 定义函数，该函数的返回值类型为(Int) -> Intfunc getMathFunc(#type:String) -> (Int) -> Int{	switch(type)	{		case "square":			return square		case "cube":			return cube		default:			return factorial	}}// 调用getMathFunc()，得到程序返回一个(Int)->Int类型的函数var mathFunc = getMathFunc(type:"cube") // 得到cube函数println(mathFunc(5)) // 输出125mathFunc = getMathFunc(type:"square") // 得到square函数println(mathFunc(5)) // 输出25mathFunc = getMathFunc(type:"other") // 得到factorial函数println(mathFunc(5)) // 输出120