// 定义函数，该函数的返回值类型为(Int) -> Intfunc getMathFunc(#type:String) -> (Int) -> Int{	// 该函数返回的是闭包	switch(type)	{		case "square":			return {(val: Int) -> Int in  // ①				return val * val			}		case "cube":			return {(val: Int) -> Int in  // ②							return val * val * val			}		default:			return {(val: Int) -> Int in  // ③				var result = 1				for index in 2...val				{					result *= index				}				return result			}	}}// 调用getMathFunc()，得到程序返回一个(Int)->Int类型的函数var mathFunc = getMathFunc(type:"cube") // 得到cube函数println(mathFunc(5)) // 输出125mathFunc = getMathFunc(type:"square") // 得到square函数println(mathFunc(5)) // 输出25mathFunc = getMathFunc(type:"other") // 得到factorial函数println(mathFunc(5)) // 输出120