// 定义一个函数，该函数返回值类型为() -> [String]func makeArray(ele:String) -> () -> [String]{	// 创建一个不包含任何元素的数组	var arr:[String] = []	func addElement() -> [String]	{		// 向arr数组中添加一个元素		arr.append(ele)		return arr	}	return addElement}println("-----add1返回的数组------")// add1将会持有arr的副本let add1 = makeArray("孙悟空")println(add1())println(add1())println("-----add2返回的数组------")// add2将会持有arr的副本，与add1的arr副本没有关系let add2 = makeArray("猪八戒")println(add2())println(add2())println("-----add3返回的数组------")// 将add2赋值给add3，实际上是让add2、add3指向同一个闭包let add3 = add2println(add3())println(add3())