class Person{	var name: String?	var gender: String?	var age = 0 	// 由于该类中前2个实例存储属性使用了可选类型，后1个实例存储属性指定了初始值，	// 因此该结构体对构造器没有要求，故此处可随便定义一个带参数的构造器	// 取消构造器参数的外部形参名	init(_ name:String , _ gender:String)	{		self.name = name		self.gender = gender	}}// 调用构造器时无需使用外部形参名var p = Person("孙悟空" , "男")  // ①println(p.name)  // 输出 孙悟空println(p.gender)  // 输出 男println(p.age)  // 输出 0