import Foundationstruct ClosureInit{	// 使用闭包对test实例存储属性执行初始化	var test: Int = {		var dt = NSDate()		var gregorian = NSCalendar.currentCalendar()		// 定义一个时间字段的旗标，指定将会获取指定月、日信息		var unitFlags = NSCalendarUnit.CalendarUnitMonth			| NSCalendarUnit.CalendarUnitDay		var comp = gregorian.components(unitFlags 			, fromDate:dt)		// 获取当前的月份		var month = Int(comp.month)		// 获取当前第几日		var day = Int(comp.day)		// 得到计算结果		return day - month	}()}var ci = ClosureInit()println(ci.test)