struct ConstructorOverload{	var name: String!	var amount: Int!	// 提供无参数的构造器	init(){ }	// 提供带两个参数的构造器来完成构造过程	init(name: String, amount: Int)	{		self.name = name		self.amount = amount	}}// 通过无参数构造器创建ConstructorOverload实例var oc1 = ConstructorOverload()// 通过有参数构造器创建ConstructorOverload实例var oc2 = ConstructorOverload(	name:"疯狂Swift讲义",amount:800000)println("\(oc1.name)  \(oc1.amount)")println("\(oc2.name)  \(oc2.amount)")	