enum Season{	case Spring, Summer, Autumn, Winter	// 使用init!定义可能失败的构造器，则该构造器创建的实例可进行隐式解析	init!(name: Character) {		// 根据传入的构造器参数选择相应的枚举成员		switch name {			case "S","s":				self = .Spring			case "U", "u":				self = .Summer			case "A", "a":				self = .Autumn			case "W", "w":				self = .Winter			// 如果传入其他参数，构造失败，返回nil。			default:				return nil		}	}}let s1 = Season(name: "s")if s1 != nil {	println("Season实例构造成功！")}let s2 = Season(name: "x")println(s2 == nil) // 输出true，表明s2为nil