// 定义枚举enum Planet{	static var weightx: Double = 2.3	// 不同枚举值可以携带数量不等、类型不等的关联值	case Mercury(weight:Double , density: Double , name:String)	case Venus(Double , Double)	case Earth(Double , String)	case Mars(density:Double , name:String)	case Jupiter	case Saturn	case Uranus	case Nepturn}var p1 = Planet.Mercury(weight:0.05 , density:5.43 , name:"水星")var p2 = Planet.Venus(0.815 , 5.25)var p3 = Planet.Earth(1.0 , "地球")var p4 = Planet.Mars(density:3.95 , name:"火星")switch(p3){	case Planet.Earth(var weight , var name):		println("该行星的名字为：\(name)，质量相当于：\(weight)个地球")	default:		break}switch(p1){	// 将Planet.Venus的关联值绑定到weight、density常量	case let .Venus(weight , density):		println("该行星的质量相当于：\(weight)个地球，密度为\(density)")	// 将Planet.Mercury的关联值绑定到w、d、n常量	case let .Mercury(weight:w , density:d , name:n):		println("该行星的名字为：\(n)，质量相当于：\(w)个地球，密度为\(d)")	default:		break}