// 使用枚举声明变量var day : Weekday// 使用已有的枚举实例赋值day = Weekday.Sunday// 由于程序可以推断day是Weekday的实例，因此可以省略枚举名前缀day = .Wednesdayprintln(day)var chooseDay = Weekday.Saturday// 使用switch语句判断枚举switch(chooseDay){	case .Monday:		println("星期一，准备上班")	case .Tuesday, .Wednesday:		println("星期二、三，放假还很远")	case .Thursday:		println("星期四，再坚持一天")	case .Friday:		println("星期五，准备放假")		case .Saturday:		println("星期六，快乐的一天")	case .Sunday:		println("星期天，这么快就过了一天？")}// 使用switch语句判断枚举switch(chooseDay){	case .Monday:		println("星期一，准备上班")	case .Tuesday, .Wednesday:		println("星期二、三，放假还很远")	default:		println("要么在放假，要么在准备放假")}