import Foundationclass Dept{	var id : Int	var info :String	init(id :Int)	{		self.id = id		// 暂停2秒，模拟耗时操作		NSThread.sleepForTimeInterval(2)		self.info = "模拟读取数据库"	}}class User{	var id:Int = 0	lazy var dept = Dept(id:20)  // ①	var nicks = [String]()}// 创建User实例var user = User()// 为user实例的nicks属性添加2个数组成员user.nicks.append("孙悟空")user.nicks.append("齐天大圣")println(user.nicks)