enum Season{	case Spring, Summer , Fall, Winter	// 定义计算属性，计算属性的本质就是getter和setter方法的组合	var info:String{		// 定义get部分代码		get {			println("正在执行getter方法")			switch(self)			{				case .Spring:					return "春暖花开"				default:					return "长恨春归无觅处"			}		}		// 定义set部分代码		set (newValue){			println("正在执行setter方法，传入参数为:\(newValue)")		}	}}var s = Season.Spring// 访问枚举的info属性，实际上就是调用对应的getter方法println(s.info)// 对枚举的info属性赋值，实际上就是调用对应的setter方法s.info = "秋高气爽"