import Foundationclass User{	var first:String = ""	var last :String = ""	// 定义计算属性	var fullName :String	{		// 定义计算属性的getter方法，该方法的返回值由first、last两个存储属性决定		get{			return first + "-" + last		}		// 定义计算属性的setter方法，		// 该setter方法的将负责改变该实例的first、last两个存储属性		set(newValue){			// Swift的String可以直接调用NSString的方法			var names = newValue.componentsSeparatedByString("-")			self.first = names[0]			self.last = names[1]		}	}	init(first:String , last:String)	{		self.first = first		self.last = last	}}let s = User(first:"悟空" , last:"孙")println(s.fullName)  // 输出 悟空-孙s.fullName = "八戒-猪"println(s.first)  // 输出 八戒println(s.last)  // 输出 猪