class Person{	// 定义存储属性	var name : String = ""{			willSet{			// 执行合理性校验，要求用户名必须在2～6位之间			if (countElements(newValue) > 6) || (countElements(newValue) < 2)			{				println("您设置的人名\(newValue)不符合要求，请重新设置！")			}			else			{				println("人名设置符合要求，设置成功！")			}		}		didSet{			println("人名设置完成，被修改的原名为：\(oldValue)")		}	}	var age : Int = 0{		willSet{			// 执行合理性校验，要求用户年龄必须在0～100之间			if newValue > 100 || newValue < 0			{				println("您设置的年龄\(newValue)不合要求，请重新设置！")			}			else			{				println("年龄设置成功！")			}		}		didSet{			println("年龄设置完成，被修改的年龄为：\(oldValue)")		}	}}var p = Person()// 下面语法设置的年龄违反了程序要求，程序会生成提示信息p.age = 1000// 成功修改p的age属性p.age = 30println("成功设置age属性后，age为：\(p.age)")  // 输出30// 成功修改p的name属性p.name = "孙悟空"println("成功设置name属性后，name为：\(p.name)")