enum Planet:Int{	case Mercury = 0, Venus, Earth, Mars, Jupiter, Saturn, Uranus, Nepturn	mutating func next()	{		if self.rawValue < 8 && self.rawValue > -1		{			self = Planet(rawValue: self.rawValue + 1)!		}	}	mutating func prev()	{		if self.rawValue < 8 && self.rawValue > -1		{			self = Planet(rawValue: self.rawValue - 1)!		}	}}	var pt = Planet.Venuspt.next()  // pt变成.Earthpt.next()  // pt变成.Marspt.prev()  // pt变成.Earth