class Customer{	var name = ""	var emp: Employee?	init(name : String)	{		self.name = name	}		// 定义一个常量类型的employees数组，用于模拟系统中所有员工	let employees = [		Employee(name:"白骨精" , title:"销售客服"),		Employee(name:"蜘蛛精" , title:"售后客服"),		Employee(name:"金鼻白鼠精" , title:"普通客服"),		Employee(name:"杏仙" , title:"销售主管")	]	// 定义一个方法，该方法可根据员工名返回对应的员工，返回值为可选类型	func findEmp(empName: String) -> Employee!	{		for emp in employees {			if emp.name == empName {				return emp			}		}		return nil	}}