var c = Customer(name:"孙悟空")var emp = Employee(name:"蜘蛛精" , title:"售后客服")// 设置Customer关联的Employee实例c.emp = emp// 设置Employee关联的Company实例emp.company = Company(name:"蛋白丝销售公司" , addr:"盘丝洞")//println("为\(c.name)服务的公司是：\(c.emp!.company.name)")println("为\(c.name)服务的公司是：\(c.emp?.company?.name)")var c2 = Customer(name:"唐僧")// 设置Customer关联的Employee实例c2.emp = Employee(name:"金鼻白鼠精" , title:"普通客服")//println("为\(c2.name)服务的公司是：\(c2.emp!.company.name)")// 使用可选链访问属性println("为\(c2.name)服务的公司是：\(c2.emp?.company?.name)")var c3 = Customer(name:"唐僧")//println("为\(c3.name)服务的公司是：\(c3.emp!.company.name)")// 使用可选链访问属性println("为\(c3.name)服务的公司是：\(c3.emp?.company?.name)")// 使用可选链调用方法c3.findEmp("白骨精")?.info()c3.findEmp("玉兔精")?.info()var dict = [Int : Customer]()dict[1] = Customer(name:"猪八戒")dict[2] = Customer(name:"沙僧")// 使用可选链访问下标dict[1]?.findEmp("杏仙")?.info()dict[4]?.findEmp("杏仙")?.info()