class Apple {	var name: String!	var weight: Double	// 定义指定构造器	init(name: String ,weight: Double)	{		self.name = name		self.weight = weight	}	// 定义便利构造器，使用convenience修饰	convenience init(n name: String , w weight: Double)	{		// 便利构造器必须调用同一个类中其他构造器		self.init(name:name , weight: weight)		self.name = name	}}var app1 = Apple(n: "红富士", w: 1.2)var app2 = Apple(name: "花牛果", weight: 2.4)