class Fruit{	var name: String	var weight: Double	init()	{		self.name = ""		self.weight = 0.0	}	init(name:String , weight: Double)	{		self.name = name		self.weight = weight	}	convenience init(name: String)	{		self.init(name:name , weight: 0.0)	}	convenience init(_ name: String)	{		self.init(name:name)	}}class Apple: Fruit{	var color: String	// 重写父类的指定构造器	override init(name: String , weight: Double)	{		self.color = "默认色"		// 调用父类构造器		super.init(name:name , weight:weight)	}	// 定义指定构造器	init(name: String)	{		self.color = "默认色"		super.init(name: name, weight: 0.0)	}	// 便利构造器	convenience init(name: String ,weight: Double,  color:String)	{		self.init(name:name , weight:weight)		self.weight = weight	}	// 使用便利构造器重写父类的指定构造器	override convenience init()	{		self.init(name:"苹果" , weight:0.0 , color:"粉红" )	}}