class Sparrow : Bird{	let weight: Double!	init?(name: String , weight: Double)	{		// 由于该构造器是可能失败的构造器，可以调用父类可能失败的构造器		super.init(name: name)		if weight <= 0 {			return nil		}		self.weight = weight		self.name = name	}	// 使用普通构造器重写父类的构造器	override init(name: String) {		// 由于该构造器是普通构造器，因此不能调用父类的可能失败的构造器		// 因此只能调用父类的普通构造器		super.init()  // ①		if name.isEmpty {			super.name = "麻雀"		}		self.name = name	}}