class Fruit{	var name: String	init(name:String)	{		self.name = name	}}class Apple: Fruit{	var color: String	init(color: String)	{//		println(self.color)  // 初始化之前不可使用本类定义的实例存储属性，编译错误		// 必须先初始化当前类定义的存储属性		self.color = color  // ①		// 可以访问当前类定义的实例存储属性		println(self.color)		// 也可对当前类定义的实例存储属性重新赋值		self.color = "红色"  // ②//		println(super.name)  // 不可使用父类定义的实例存储属性，编译错误		super.init(name: "花牛果")  // ③		// 可以访问父类类定义的实例存储属性		println(super.name)		// 也可对父类定义的实例存储属性重新赋值		super.name = "红富士"  // ④	}}var a = Apple(color: "绿色")