class Fruit{	var name: String	init(name:String)	{		self.name = name	}}class Apple: Fruit{	var color: String	init(color: String)	{		self.color = color		println(self.color)		super.init(name: "红富士")	}	// 定义便利构造器	convenience init(name: String , color: String)	{		// 调用其他构造器之前，不能访问或修改任何实例存储属性，下面两行代码出错//		println(self.color)//		super.name = name		// 调用本类其他构造器		self.init(color:"红色")	}}var a = Apple(color: "red")