class Fruit{	var name: String	var weight: Double	init(name: String , weight:Double)	{		self.name = name		self.weight = weight	}}class Apple : Fruit{	var color : String	init(name:String , weight:Double , color:String)	{		self.color = color		super.init(name:name , weight:weight)	}}class Grape: Fruit{	var sugarRate: Double	init(name: String , weight: Double , sugarRate: Double)	{		self.sugarRate = sugarRate		super.init(name: name, weight: weight)	}}// 定义元素为Any的数组，该数组的元素几乎可以放置任何数据var anyArray:[Any] = [	"swift",	29,	["ios": 89 , "android": 92],	Fruit(name: "樱桃" , weight: 2.3),	Apple(name:"红富士" , weight:2.4 , color:"粉红")]// 遍历元素为Any的数组for element in anyArray {	// 尝试将数组元素强转为Fruit，然后执行可选绑定	if let f = element as? Fruit {		println("\(f.name)水果重\(f.weight)")	}}// 定义元素为AnyObject的数组，该数组的元素只能是对象var anyObjectArray:[AnyObject] = [	Fruit(name: "樱桃" , weight: 2.3),	Apple(name:"红富士" , weight:2.4 , color:"粉红")]// 这行代码将导致错误//anyObjectArray.append("Swift")  // ①