class Fruit{	var name: String	var weight: Double	init(name: String , weight:Double)	{		self.name = name		self.weight = weight	}}class Apple : Fruit{	var color : String	init(name:String , weight:Double , color:String)	{		self.color = color		super.init(name:name , weight:weight)	}}class Grape: Fruit{	var sugarRate: Double	init(name: String , weight: Double , sugarRate: Double)	{		self.sugarRate = sugarRate		super.init(name: name, weight: weight)	}}// 使用数组保存4个水果var fruits:[Fruit] = [	Apple(name: "红富士", weight: 1.8, color: "粉红"),	Apple(name: "花牛果", weight: 2.3, color: "红色"),	Grape(name: "巨峰葡萄", weight: 1.4, sugarRate: 0.34),	Grape(name: "加州提子", weight: 2.2, sugarRate: 0.45)]// 由于fruits的类型是[Fruit]，因此程序只知道该数组元素是Fruitfor f in fruits {	// 此处先使用as?执行向下转向，返回可选值，然后使用可选绑定	if let ap = f as? Apple {		println("\(ap.name)苹果的颜色是：\(ap.color)")	}	else if let gp = f as? Grape {		println("\(gp.name)葡萄的糖分是：\(gp.sugarRate)")	}}