class User{	var holidays: [Weekday]	var location:Point	init(holidays:[Weekday] , location:Point)	{		self.holidays = holidays		self.location = location	}	// 定义一个嵌套类型：结构体Point	struct Point	{		var latitude: Double		var longitude: Double		var position: Orientation		// 嵌套枚举		enum Orientation		{			case Up, Left , Bottom , Right		}	}	// 定义一个嵌套类型：枚举Weekday	enum Weekday	{		case Monday, Tuesday, Wednesday, Thursday, Friday,Saturday ,Sunday		struct Duration		{			var num: Double			var unit: String		}	}}// 使用嵌套类型时，需要以被嵌套类型的名字为前缀。var user = User(holidays: [], location: 	User.Point(latitude: -23.33, longitude: 122.11, 	position: User.Point.Orientation.Left))// 引用User.Weekday嵌套枚举user.holidays.append(User.Weekday.Saturday)user.holidays.append(User.Weekday.Sunday)