extension Array{	// 定义一个方法，用于计算数组的交集	mutating func retainAll(array: [T], compartor: (T, T) -> Bool)	{		var tmp = [T]()		// 遍历当前数组中所有元素		for ele in self		{			// 遍历第二个数组的所有元素			for target in array			{				// 如果两个元素通过compartor比较返回true				if compartor(ele , target)				{					tmp.append(ele)					break				}			}		}		self = tmp	}}var books = ["iOS" , "Android" , "Swift" , "Java" , "Ruby"]books.retainAll(["Android", "iOS" , "C"]){	return $0 == $1}println(books)  // 输出 [iOS, Android] 