// 让Circle实现2个协议class Circle: HasArea, Mathable{	var radius: Double	init(radius:Double)	{		self.radius = radius	}	// 使用存储属性实现Fullable协议的fullColor属性	var fullColor: Color?	// 使用存储属性实现Strokable协议的strokeWidth属性	var strokeWidth: Double = 0.0	// 使用存储属性实现HashArea协议要求的area属性	var area: Double {		get{			return Circle.pi * radius * radius		}	}	// 通过存储属性实现Mathable协议要求的两个类型属性	// 由于类不支持定义类型存储属性，只能使用类型计算属性	class var pi:Double { return 3.14159535 }	class var e:Double { return  2.71828 }}