// 让Rect实现2个协议struct Rect: HasArea, Mathable{	var width: Double	var height: Double	init(width:Double , height:Double)	{		self.width = width		self.height = height	}	// 使用存储属性实现Fullable协议的fullColor属性	var fullColor: Color?	// 使用存储属性实现Strokable协议的strokeWidth属性	var strokeWidth: Double = 0.0	// 使用存储属性实现HashArea协议要求的area属性	var area: Double {		get{			return width * height		}	}	// 通过存储属性实现Mathable协议要求的两个类型属性	// 虽然协议中使用class关键字定义类型属性，但值类型中依然使用static定义类型属性	static var pi:Double = 3.14159535	static var e:Double = 2.71828}