class Student{	var name : String	var age: Int	// 程序将self传入studentInfo属性值对应的闭包中	// 该闭包机会捕获self所代表的对象	lazy var stuInfo : () -> String = {		"学生名为：\(self.name)，年龄为：\(self.age)"	}	init(name:String , age: Int)	{		self.name = name		self.age = age	}	// 定义析构器	deinit {		println("\(self.name)学生即将被销毁")	}}// 定义stu引用变量，并将Student对象赋值给stu变量var stu: Student? = Student(name:"孙悟空" , age: 500)// 定义函数类型的变量，并将闭包赋值给该变量var info: (() -> String)? = stu!.stuInfostu = nilinfo = nil