class Student{	var name : String	var age: Int	lazy var stuInfo : () -> String = {		// 定义捕获列表，指定闭包中self引用变量是无主引用		[unowned self] in		"\(self.name),\(self.age)"	}	init(name:String , age: Int)	{		self.name = name		self.age = age	}	// 定义析构器	deinit {		println("\(self.name)学生即将被销毁")	}}var stu: Student? = Student(name:"孙悟空" , age: 500)var info: (() -> String)? = stu!.stuInfo  // ①stu = nilinfo = nil  // ②