class Student{	var name : String	var age: Int	// 使用可选属性引用关联的Coach实例	var coach: Coach?	init(name:String , age: Int)	{		self.name = name		self.age = age	}	// 定义析构器	deinit {		println("\(self.name)学生即将被销毁")	}	}class Coach{	var name: String	var skill: String	// 使用可选属性引用关联的Student实例	var student: Student?	init(name:String , skill: String)	{		self.name = name		self.skill = skill	}	// 定义析构器	deinit {		println("\(self.name)教练即将被销毁")	}	}// 定义stu变量引用Student对象var stu:Student? = Student(name:"孙悟空" ,age: 500)// 定义coach变量引用Coach对象var coach:Coach? = Coach(name:"唐僧" , skill:"念经")  // ①// 建立stu与coach的关联关系stu?.coach = coachcoach?.student = stu// 将stu、coach两个变量赋值为nilstu = nilcoach = nil