class Student{	var name : String	var age: Int	var coach: Coach?	init(name:String , age: Int)	{		self.name = name		self.age = age	}	// 定义析构器	deinit {		println("\(self.name)学生即将被销毁")	}	}class Coach{	var name: String	var skill: String	// 使用弱引用持有Student对象	weak var student: Student?	init(name:String , skill: String)	{		self.name = name		self.skill = skill	}	// 定义析构器	deinit {		println("\(self.name)教练即将被销毁")	}}var stu:Student? = Student(name:"孙悟空" ,age: 500)var coach:Coach? = Coach(name:"唐僧" , skill:"念经")stu?.coach = coachcoach?.student = stustu = nilcoach = nil