func copyIntArray(src:[Int] , inout dest: [Int]){	// 遍历src数组，并将src数组的元素追加到dest数组后面	for element in src	{		dest.append(element)	}}var arr = [29 , 2]// 将前一个数组的元素追加到后一个数组中copyIntArray([12, 8], &arr)println(arr) // 输出 [29, 2, 12, 8]func copyStringArray(src:[String] , inout dest: [String]){	// 遍历src数组，并将src数组的元素追加到dest数组后面	for element in src	{		dest.append(element)	}}var strArr = ["Swift" , "iOS"]// 将前一个数组的元素追加到后一个数组中copyStringArray(["Android", "C"], &strArr)println(strArr) // 输出 [Swift, iOS, Android, C]