func copyArray <T> (src:[T] , inout dest: [T]){	// 遍历src数组，并将src数组的元素追加到dest数组后面	for element in src	{		dest.append(element)	}}var arr = [29 , 2]// 将前一个数组的元素追加到后一个数组中copyArray([12, 8], &arr) // 此时T的类型代表Intprintln(arr) // 输出 [29, 2, 12, 8]var strArr = ["Swift" , "iOS"]// 将前一个数组的元素追加到后一个数组中copyArray(["Android", "C"], &strArr)  // 此时T的类型代表Stringprintln(strArr) // 输出 [Swift, iOS, Android, C]var doubleArray = [1.2 , 4.5]// 将前一个数组的元素追加到后一个数组中copyArray([8.3, -1.2], &doubleArray)  // 此时T的类型代表Doubleprintln(doubleArray) // 输出 [1.2, 4.5, 8.3, -1.2]