// 定义冒泡排序算法func bubbleSort <T: Comparable> (inout array: [T]){	// 获取数组的长度	let len = array.count	// 定义本趟比较是否发生交换的旗标	var hasSwap = true	for var i = 0 ; i < len - 1 && hasSwap; i++	{		hasSwap = false		// 如果两个“挨着”的数并非由小到大，交换它们		for var j = 0 ; j < len - i - 1 ; j++		{			if array[j] > array[j + 1]			{				let tmp = array[j]				array[j] = array[j + 1]				array[j + 1] = tmp				hasSwap = true			}		}	}}var myArr = [-2, 12, 20, 9, 6, -40, 23, -19, 34, 78, 10]bubbleSort(&myArr)  // ①println(myArr)var doubleArr = [-2.1, 2.0, 3.9, 9.2, 6.4, -41.2, 23.5, -19.8, 34.7]bubbleSort(&doubleArr)  // ②println(doubleArr)var strArr = ["zhao", "qian", "sun", "li", "zhou", "wu", "zhen", "wang"]bubbleSort(&strArr)  // ③println(strArr)