// 定义冒泡排序算法func intBubbleSort(inout array: [Int]){	// 获取数组的长度	let len = array.count	// 定义本趟比较是否发生交换的旗标	var hasSwap = true	for var i = 0 ; i < len - 1 && hasSwap; i++	{		hasSwap = false		// 如果两个“挨着”的数并非由小到大，交换它们		for var j = 0 ; j < len - i - 1 ; j++		{			if array[j] > array[j + 1]			{				let tmp = array[j]				array[j] = array[j + 1]				array[j + 1] = tmp				hasSwap = true			}		}	}}var myArr = [-2, 12, 20, 9, 6, -40, 23, -19, 34, 78, 10]intBubbleSort(&myArr)println(myArr)