// 声明新的运算符，指定该运算符的结核性为左结合，并指定其优先级为90infix operator **= { associativity left precedence 90 }// 为新的运算符定义函数func **= (inout base: Int, exponent: Int) {	var result = 1	for _ in 1 ... exponent	{		result *= base	}	base = result}// 为新的运算符定义函数func **= (inout base: Double, exponent: Int)  {	var result = 1.0	for _ in 1 ... exponent	{		result *= base	}	base = result}var a = 20let b = 2a **= bprintln("a的值为：\(a)")var c = 2.4let d = 3c **= dprintln("c的值为：\(c)")