import Foundationvar book = "《疯狂iOS讲义》"// 创建一个NSMutableString对象let str = NSMutableString(string:"Hello")// 追加固定字符串// 字符串所包含的字符序列本身发生了改变，因此无须重新赋值str.appendString(",iOS!")println(str)str.appendString(book)// 在指定位置插入字符串// 字符串所包含的字符序列本身发生了改变，因此无须重新赋值str.insertString("fkit.org" , atIndex:6)println(str)// 删除从位置6开始、长度为12的所有字符str.deleteCharactersInRange(NSMakeRange(6, 12))println(str)// 将从位置6开始、长度为9的所有字符替换成Objective-Cstr.replaceCharactersInRange(NSMakeRange(6, 9) , withString:"Objetive-C")println(str)