import Foundation// 获取代表公历的Calendar对象let gregorian = NSCalendar(calendarIdentifier:NSGregorianCalendar)// 获取当前日期let dt = NSDate()// 定义一个时间字段的旗标，指定将会获取指定年、月、日、时、分、秒的信息let unitFlags = NSCalendarUnit.CalendarUnitYear |	NSCalendarUnit.CalendarUnitMonth | NSCalendarUnit.CalendarUnitDay |	NSCalendarUnit.CalendarUnitHour | NSCalendarUnit.CalendarUnitMinute |	NSCalendarUnit.CalendarUnitSecond | NSCalendarUnit.CalendarUnitWeekday// 获取不同时间字段的信息let comp = gregorian!.components(unitFlags, fromDate:dt)// 获取各时间字段的数值println("现在是\(comp.year)年")println("现在是\(comp.month)月 ")println("现在是\(comp.day)日")println("现在是\(comp.hour)时")println("现在是\(comp.minute)分")println("现在是\(comp.second)秒")println("现在是星期\(comp.weekday)")// 再次创建一个NSDateComponents对象let comp2 = NSDateComponents()// 设置各时间字段的数值comp2.year = 2013comp2.month = 4comp2.day = 5comp2.hour = 18comp2.minute = 34// 通过NSDateComponents所包含的时间字段的数值来恢复NSDate对象let date = gregorian!.dateFromComponents(comp2)println("获取的日期为：\(date)")