import Foundation// 获取代表当前日期、时间的NSDatelet date1 = NSDate()println(date1)// 获取从当前时间开始，一天之后的日期let date2 = NSDate(timeIntervalSinceNow:3600*24)println(date2)// 获取从当前时间开始，3天之前的日期let date3 = NSDate(timeIntervalSinceNow: -3*3600*24)println(date3)// 获取从1970年1月1日开始，20年之后的日期let date4 = NSDate(timeIntervalSince1970:	3600 * 24 * 366 * 20)println(date4)// 获取系统当前的Localelet cn = NSLocale.currentLocale()// 获取NSDate在当前Locale下对应的字符串println(date1.descriptionWithLocale(cn))// 获取两个日期之间较早的日期let earlier = date1.earlierDate(date2)// 获取两个日期之间较晚的日期let later = date1.laterDate(date2)// 比较两个日期，compare()方法返回NSComparisonResult枚举值，// 该枚举类型包含OrderedAscending、OrderedSame和OrderedDescending三个值，// 分别代表调用compare()的日期位于被比较日期之前、相同、之后switch date1.compare(date3){	case .OrderedAscending:		println("date1位于date3之前")	case .OrderedSame:		println("date1与date3日期相等")	case .OrderedDescending:		println("date1位于date3之后")}// 获取两个时间之间的时间差NSLog("date1与date3之间时间差%g秒"	, date1.timeIntervalSinceDate(date3))// 获取指定时间与现在的时间差NSLog("date2与现在间时间差%g秒"	, date2.timeIntervalSinceNow)