import Foundationvar array1:NSArray = ["Objective-C" , "C" , "C++" 	, "Ruby" , "Perl" , "Swift"]	// 使用集合元素的compare:方法进行排序array1 = array1.sortedArrayUsingSelector("compare:")println(array1)// 初始化一个元素为Int的NSArray对象let array2:NSArray = [20 , 12 , -8 , 50 , 19]// 使用闭包对集合元素进行排序let array3 = array2.sortedArrayUsingComparator(){(obj1: AnyObject!, obj2: AnyObject!) -> NSComparisonResult in	// 该闭包就是根据集合元素本身的大小进行比较	if (obj1 as? Int)! > (obj2 as? Int)!	{		return .OrderedDescending	}	if (obj1 as? Int)! < (obj2 as? Int)! 	{		return .OrderedAscending	}	return .OrderedSame}println(array3)